svg.script:
class Calculator {
  constructor() {
    this.display = document.getElementById('display');
    this.currentValue = '0';
    this.previousValue = null;
    this.operation = null;
    this.waitingForNewValue = false;

    // ObsÅ‚uga klawiatury
    document.addEventListener('keydown', (e) => this.handleKeyboard(e));

    // PWA features
    this.initPWA();

    console.log('ðŸ“± Kalkulator SVG PWA zaÅ‚adowany');
  }

  updateDisplay() {
    // Formatuj liczbÄ™
    let displayValue = this.currentValue;
    if (displayValue.length > 12) {
      displayValue = parseFloat(displayValue).toExponential(6);
    }
    this.display.textContent = displayValue;
  }

  inputNumber(num) {
    if (this.waitingForNewValue) {
      this.currentValue = num;
      this.waitingForNewValue = false;
    } else {
      this.currentValue = this.currentValue === '0' ? num : this.currentValue + num;
    }
    this.updateDisplay();
  }

  inputDecimal() {
    if (this.waitingForNewValue) {
      this.currentValue = '0.';
      this.waitingForNewValue = false;
    } else if (this.currentValue.indexOf('.') === -1) {
      this.currentValue += '.';
    }
    this.updateDisplay();
  }

  clear() {
    this.currentValue = '0';
    this.previousValue = null;
    this.operation = null;
    this.waitingForNewValue = false;
    this.updateDisplay();
  }

  setOperation(op) {
    if (this.previousValue === null) {
      this.previousValue = this.currentValue;
    } else if (this.operation) {
      this.calculate();
      this.previousValue = this.currentValue;
    }

    this.operation = op;
    this.waitingForNewValue = true;
  }

  calculate() {
    if (this.operation && this.previousValue !== null) {
      const prev = parseFloat(this.previousValue);
      const current = parseFloat(this.currentValue);
      let result = 0;

      switch (this.operation) {
        case '+':
          result = prev + current;
          break;
        case '-':
          result = prev - current;
          break;
        case '*':
          result = prev * current;
          break;
        case '/':
          result = current !== 0 ? prev / current : 0;
          break;
      }

      this.currentValue = result.toString();
      this.previousValue = null;
      this.operation = null;
      this.waitingForNewValue = true;
      this.updateDisplay();
    }
  }

  toggleSign() {
    this.currentValue = (parseFloat(this.currentValue) * -1).toString();
    this.updateDisplay();
  }

  percent() {
    this.currentValue = (parseFloat(this.currentValue) / 100).toString();
    this.updateDisplay();
  }

  handleKeyboard(e) {
    e.preventDefault();

    if (e.key >= '0' && e.key <= '9') {
      this.inputNumber(e.key);
    } else if (e.key === '.') {
      this.inputDecimal();
    } else if (e.key === '+') {
      this.setOperation('+');
    } else if (e.key === '-') {
      this.setOperation('-');
    } else if (e.key === '*') {
      this.setOperation('*');
    } else if (e.key === '/') {
      this.setOperation('/');
    } else if (e.key === 'Enter' || e.key === '=') {
      this.calculate();
    } else if (e.key === 'Escape' || e.key === 'c' || e.key === 'C') {
      this.clear();
    }
  }

  initPWA() {
    // Symulacja service worker dla SVG
    if ('serviceWorker' in navigator) {
      // W prawdziwej aplikacji tutaj byÅ‚aby rejestracja service worker
      console.log('ðŸ”§ PWA features available');
    }

    // Sprawdzenie czy moÅ¼na zainstalowaÄ‡
    window.addEventListener('beforeinstallprompt', (e) => {
      console.log('ðŸ“² App can be installed');
      e.preventDefault();
      // MoÅ¼liwoÅ›Ä‡ pokazania prompt install
    });

    // Historia dziaÅ‚aÅ„ dla offline
    this.history = JSON.parse(localStorage.getItem('calc-history') || '[]');
  }

  saveToHistory(operation) {
    this.history.push({
      operation: operation,
      result: this.currentValue,
      timestamp: new Date().toISOString()
    });

    // Zachowaj tylko ostatnie 10 operacji
    if (this.history.length > 10) {
      this.history = this.history.slice(-10);
    }

    localStorage.setItem('calc-history', JSON.stringify(this.history));
  }
}

// Inicjalizacja
const calculator = new Calculator();

// Manifest PWA (embedded)
const pwaManifest = {
  "name": "Kalkulator SVG PWA",
  "short_name": "CalcSVG",
  "description": "Prosty kalkulator w formacie SVG",
  "start_url": ".",
  "display": "standalone",
  "background_color": "#667eea",
  "theme_color": "#764ba2",
  "orientation": "portrait",
  "icons": [
    {
      "src": "data:image/svg+xml;base64,",
      "sizes": "192x192",
      "type": "image/svg+xml"
    }
  ]
};

console.log('ðŸ“± PWA Manifest:', pwaManifest);
